import React, {useState} from 'react'

import {Nav, NavbarContainer, NavLogo, NavMenu, NavLinks, NavItem} from './NarbarElements'


const Navbar = () => {
    const [scrollNav, setScrollNav] = useState(false);

    const changeNav = () => {
        if(window.scrollY >= 80){
            setScrollNav(true)
        } else{
            setScrollNav(false)
        }
    }

    useEffect(() => {
        window.addEventListener('scroll', changeNav, [])
    })
    return (
        <>
            <Nav>
                <NavbarContainer>
                    <NavLogo>
                        blockchain-tracker
                    </NavLogo>
                    <NavMenu>
                        <NavItem>
                            <NavLinks to='about'>About</NavLinks>
                        </NavItem>
                        <NavItem>
                            <NavLinks to='blockchain'>Blockchain</NavLinks>
                        </NavItem>
                        <NavItem>
                            <NavLinks to='track-expense'>Track Expenses</NavLinks>
                        </NavItem>
                    </NavMenu>
                </NavbarContainer>
            </Nav>
        </>
    )
}

export default Navbar
