        dispatch({
            type: 'ADD_TRANSACTION',
            payload: transaction
        });
    }
    function web3Setup(web3){
        dispatch({
            type: 'SETUP_WEB3',
            payload: web3
        });
    }
    function contractSetup(contract){
        dispatch({
            type: 'SETUP_CONTRACT',
            payload: web3
        });
    }




    return (
    <GlobalContext.Provider value={{transactions:state.transactions, deleteTransaction, addTransaction}}>
        {children}
    </GlobalContext.Provider>);

}
